import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiUpload, FiFile, FiCheck, FiX, FiLoader } from 'react-icons/fi';
import { FaRegSmile, FaRegFrown } from 'react-icons/fa';
import Confetti from 'react-confetti';


function App() {
  const [file, setFile] = useState(null);
  const [quizType, setQuizType] = useState('mcq');
  const [quizData, setQuizData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [userAnswers, setUserAnswers] = useState({});
  const [results, setResults] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const fileInputRef = useRef(null);

  const quizTypes = [
    { id: 'mcq', name: 'Multiple Choice', description: '4 options per question' },
    { id: 'tf', name: 'True/False', description: 'Simple true or false questions' },
    { id: 'fib', name: 'Fill in Blank', description: 'Complete the missing words' }
  ];

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      const allowedTypes = ['application/pdf', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document', 'text/plain'];
      if (allowedTypes.includes(selectedFile.type)) {
        setFile(selectedFile);
        setError(null);
      } else {
        setError('Please upload a PDF, DOCX, or TXT file');
      }
    }
  };

  const triggerFileInput = () => {
    fileInputRef.current.click();
  };

  const onSubmit = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setIsLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('quiz_type', quizType);

    try {
      const response = await fetch('http://localhost:8000/generate-quiz', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to generate quiz');
      }

      const data = await response.json();
      setQuizData(data.quiz);
      setUserAnswers({});
      setResults(null);
    } catch (err) {
      setError(err.message || 'Something went wrong');
    } finally {
      setIsLoading(false);
    }
  };

  const handleAnswerSelect = (questionIndex, answer) => {
    setUserAnswers(prev => ({
      ...prev,
      [questionIndex]: answer
    }));
  };

  const checkResults = () => {
    if (!quizData) return;

    let correct = 0;
    const questionResults = quizData.map((question, index) => {
      const isCorrect = userAnswers[index] === question.answer;
      if (isCorrect) correct++;
      return {
        question: question.question,
        userAnswer: userAnswers[index],
        correctAnswer: question.answer,
        isCorrect
      };
    });

    const score = Math.round((correct / quizData.length) * 100);
    setResults({ score, details: questionResults });

    if (score >= 80) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000);
    }
  };

  const resetQuiz = () => {
    setQuizData(null);
    setUserAnswers({});
    setResults(null);
    setFile(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-blue-100 py-12 px-4 sm:px-6 lg:px-8">
      {showConfetti && (
        <Confetti
          width={windowSize.width}
          height={windowSize.height}
          recycle={false}
          numberOfPieces={500}
          gravity={0.2}
        />
      )}

      <div className="max-w-3xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          <h1 className="text-4xl font-extrabold text-gray-900 mb-2 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600">
            AI Quiz Generator
          </h1>
          <p className="text-lg text-gray-600">
            Upload your document and get an instant quiz to test your knowledge
          </p>
        </motion.div>

        <AnimatePresence>
          {!quizData ? (
            <motion.div
              key="form"
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.95 }}
              transition={{ duration: 0.3 }}
              className="bg-white rounded-xl shadow-lg p-6 mb-8"
            >
              <div className="space-y-6">
                <div>
                  <h2 className="text-xl font-semibold text-gray-800 mb-3">Upload Your Document</h2>
                  <div 
                    onClick={triggerFileInput}
                    className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center cursor-pointer hover:border-blue-500 transition-colors duration-200"
                  >
                    <input
                      type="file"
                      ref={fileInputRef}
                      onChange={handleFileChange}
                      className="hidden"
                      accept=".pdf,.docx,.txt"
                    />
                    {file ? (
                      <div className="flex items-center justify-center space-x-3">
                        <FiFile className="text-blue-500 text-2xl" />
                        <span className="text-gray-700 font-medium">{file.name}</span>
                        <FiCheck className="text-green-500" />
                      </div>
                    ) : (
                      <>
                        <FiUpload className="mx-auto text-3xl text-gray-400 mb-3" />
                        <p className="text-gray-500">Click to upload or drag and drop</p>
                        <p className="text-sm text-gray-400 mt-1">PDF, DOCX, or TXT files only</p>
                      </>
                    )}
                  </div>
                </div>

                <div>
                  <h2 className="text-xl font-semibold text-gray-800 mb-3">Select Quiz Type</h2>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {quizTypes.map((type) => (
                      <motion.div
                        key={type.id}
                        whileHover={{ scale: 1.03 }}
                        whileTap={{ scale: 0.98 }}
                        className={`p-4 rounded-lg border cursor-pointer transition-all duration-200 ${quizType === type.id ? 'border-blue-500 bg-blue-50' : 'border-gray-200 hover:border-gray-300'}`}
                        onClick={() => setQuizType(type.id)}
                      >
                        <h3 className="font-medium text-gray-800">{type.name}</h3>
                        <p className="text-sm text-gray-500 mt-1">{type.description}</p>
                        {quizType === type.id && (
                          <div className="mt-2 w-full bg-blue-500 h-1 rounded-full"></div>
                        )}
                      </motion.div>
                    ))}
                  </div>
                </div>

                {error && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="bg-red-50 border-l-4 border-red-500 p-4"
                  >
                    <div className="flex items-center">
                      <FiX className="text-red-500 mr-2" />
                      <p className="text-red-700">{error}</p>
                    </div>
                  </motion.div>
                )}

                <div className="pt-2">
                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    onClick={onSubmit}
                    disabled={isLoading || !file}
                    className={`w-full flex items-center justify-center py-3 px-4 rounded-lg font-medium text-white ${isLoading || !file ? 'bg-blue-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'} transition-colors duration-200`}
                  >
                    {isLoading ? (
                      <>
                        <FiLoader className="animate-spin mr-2" />
                        Generating Quiz...
                      </>
                    ) : (
                      'Generate Quiz'
                    )}
                  </motion.button>
                </div>
              </div>
            </motion.div>
          ) : (
            <motion.div
              key="quiz"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="bg-white rounded-xl shadow-lg overflow-hidden"
            >
              {!results ? (
                <div className="p-6">
                  <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold text-gray-800">Your Generated Quiz</h2>
                    <button
                      onClick={resetQuiz}
                      className="text-sm text-blue-600 hover:text-blue-800 font-medium"
                    >
                      Start Over
                    </button>
                  </div>

                  <div className="space-y-8">
                    {quizData.map((question, index) => (
                      <div key={index} className="border-b border-gray-100 pb-6 last:border-0 last:pb-0">
                        <h3 className="text-lg font-medium text-gray-800 mb-4">
                          <span className="text-blue-600 mr-2">{index + 1}.</span>
                          {question.question}
                        </h3>

                        {question.options.length > 0 ? (
                          <div className="space-y-3">
                            {question.options.map((option, optionIndex) => (
                              <motion.div
                                key={optionIndex}
                                whileHover={{ x: 5 }}
                                className={`px-4 py-3 rounded-lg cursor-pointer transition-colors duration-200 ${userAnswers[index] === option ? 'bg-blue-100 border border-blue-300' : 'bg-gray-50 hover:bg-gray-100 border border-gray-200'}`}
                                onClick={() => handleAnswerSelect(index, option)}
                              >
                                <div className="flex items-center">
                                  <div className={`w-5 h-5 rounded-full border flex items-center justify-center mr-3 ${userAnswers[index] === option ? 'border-blue-500 bg-blue-500' : 'border-gray-400'}`}>
                                    {userAnswers[index] === option && (
                                      <div className="w-2 h-2 rounded-full bg-white"></div>
                                    )}
                                  </div>
                                  <span className="text-gray-700">{option}</span>
                                </div>
                              </motion.div>
                            ))}
                          </div>
                        ) : (
                          <div className="relative">
                            <input
                              type="text"
                              value={userAnswers[index] || ''}
                              onChange={(e) => handleAnswerSelect(index, e.target.value)}
                              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none"
                              placeholder="Type your answer here..."
                            />
                          </div>
                        )}
                      </div>
                    ))}
                  </div>

                  <div className="mt-8">
                    <motion.button
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                      onClick={checkResults}
                      disabled={Object.keys(userAnswers).length !== quizData.length}
                      className={`w-full py-3 px-4 rounded-lg font-medium text-white ${Object.keys(userAnswers).length !== quizData.length ? 'bg-blue-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'} transition-colors duration-200`}
                    >
                      Check Results
                    </motion.button>
                  </div>
                </div>
              ) : (
                <div className="p-6">
                  <div className="text-center mb-8">
                    <div className="inline-flex items-center justify-center w-24 h-24 rounded-full bg-gradient-to-r from-blue-100 to-indigo-100 mb-4">
                      {results.score >= 80 ? (
                        <FaRegSmile className="text-4xl text-green-500" />
                      ) : (
                        <FaRegFrown className="text-4xl text-yellow-500" />
                      )}
                    </div>
                    <h2 className="text-3xl font-bold text-gray-800 mb-2">
                      {results.score >= 80 ? 'Great Job!' : 'Good Try!'}
                    </h2>
                    <p className="text-lg text-gray-600 mb-4">
                      You scored <span className={`font-bold ${results.score >= 80 ? 'text-green-600' : 'text-yellow-600'}`}>{results.score}%</span>
                    </p>
                    <button
                      onClick={resetQuiz}
                      className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Try Another Quiz
                    </button>
                  </div>

                  <div className="space-y-6">
                    {results.details.map((result, index) => (
                      <div key={index} className={`p-4 rounded-lg ${result.isCorrect ? 'bg-green-50' : 'bg-red-50'}`}>
                        <div className="flex items-start">
                          <div className={`flex-shrink-0 h-6 w-6 rounded-full flex items-center justify-center ${result.isCorrect ? 'bg-green-100 text-green-600' : 'bg-red-100 text-red-600'}`}>
                            {result.isCorrect ? <FiCheck /> : <FiX />}
                          </div>
                          <div className="ml-3">
                            <h3 className="text-sm font-medium text-gray-800">
                              {result.question}
                            </h3>
                            <div className="mt-1 text-sm text-gray-600">
                              {!result.isCorrect && (
                                <p>Your answer: <span className="font-medium">{result.userAnswer || 'No answer provided'}</span></p>
                              )}
                              <p>Correct answer: <span className="font-medium">{result.correctAnswer}</span></p>
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>

        <motion.footer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
          className="text-center text-gray-500 text-sm mt-12"
        >
          <p>AI Quiz Generator - Powered by Azure OpenAI</p>
        </motion.footer>
      </div>
    </div>
  );
}

export default App;
