// Full App.js with timer, answer capture, scoring, and no in‚Äëplace question editing
import React, { useState, useEffect } from "react";
import { jsPDF } from "jspdf";
import "./App.css";

// ---------------------------------------------
// Quiz type meta
// ---------------------------------------------
const quizTypes = [
  { label: "Multiple Choice", value: "mcq" },
  { label: "True / False", value: "tf" },
  { label: "Fill in the Blanks", value: "fib" },
  { label: "Video Based", value: "video" },
  { label: "URL Based", value: "url" },
];

// ---------------------------------------------
// Main component
// ---------------------------------------------
export default function App() {
  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Global state
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const [step, setStep] = useState("auth");
  const [authStep, setAuthStep] = useState("login");

  // Auth
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // Quiz generation inputs
  const [file, setFile] = useState(null);
  const [url, setUrl] = useState("");
  const [quizType, setQuizType] = useState("mcq");

  // Quiz + UI state
  const [quiz, setQuiz] = useState([]);                  // generated questions
  const [quizSource, setQuizSource] = useState("");      // file name / url label
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  // User profile + history
  const [userProfile, setUserProfile] = useState(null);
  const [showProfile, setShowProfile] = useState(false);
  const [quizHistory, setQuizHistory] = useState([]);

  // PDF handling
  const [pdfBlob, setPdfBlob] = useState(null);
  const [pdfUrl, setPdfUrl]   = useState("");
  const [showPdfModal, setShowPdfModal] = useState(false);

  // Timer / quiz‚Äëtaking state
  const [timeLeft, setTimeLeft]   = useState(300);   // default 5 min
  const [timerActive, setTimerActive] = useState(false);
  const [userAnswers, setUserAnswers] = useState({}); // { [qIdx]: answer }
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(null);           // { total, correct }

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Utility helpers
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const isValidUrl = (urlString) => {
    try { new URL(urlString); return true; } catch { return false; }
  };

  const getCurrentPageTitle = () => {
    switch (step) {
      case "upload":  return "Create New Quiz";
      case "preview": return "Quiz";
      case "auth":    return authStep === "login" ? "Login" : "Register";
      default:         return "AI Quiz Generator";
    }
  };

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Auth handlers
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const handleRegister = async () => {
    try {
      const res = await fetch("http://localhost:8000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        alert("Registration successful! Please login.");
        setAuthStep("login");
      } else {
        alert(data.message || "Registration failed");
      }
    } catch (error) {
      alert("Registration error: " + error.message);
    }
  };

  const handleLogin = async () => {
    try {
      const formData = new URLSearchParams();
      formData.append("username", username);
      formData.append("password", password);

      const res = await fetch("http://localhost:8000/login", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();

      if (res.ok) {
        setUserProfile({
          username,
          email: `${username}@example.com`,
          joinedDate: new Date().toISOString().split("T")[0],
          quizzesGenerated: 0,
          downloads: 0,
          lastActive: new Date().toLocaleDateString(),
        });
        setStep("upload");
      } else {
        alert(data.message || "Login failed");
      }
    } catch (error) {
      alert("Login error: " + error.message);
    }
  };

  const handleLogout = () => {
    setStep("auth");
    setUsername("");
    setPassword("");
    setUserProfile(null);
    setShowProfile(false);
  };

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Quiz generation handlers
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const handleUpload = async () => {
    if ((quizType !== "url" && !file) || (quizType === "url" && !url)) {
      alert("Please provide the required input");
      return;
    }
    if (quizType === "url" && !isValidUrl(url)) {
      alert("Please enter a valid URL");
      return;
    }

    setLoading(true);

    const formData = new FormData();
    if (file) formData.append("file", file);
    formData.append("quiz_type", quizType);
    if (url) formData.append("url", url);

    try {
      const res = await fetch("http://localhost:8000/generate-quiz", {
        method: "POST",
        body: formData,
      });
      if (!res.ok) {
        const err = await res.json();
        throw new Error(err.detail || "Failed to generate quiz");
      }
      const data = await res.json();
      setQuiz(data.quiz);
      setQuizSource(quizType === "url" ? url : file ? file.name : "content");
      setStep("preview");

      // profile counters
      setUserProfile((prev) => ({
        ...prev,
        quizzesGenerated: (prev?.quizzesGenerated || 0) + 1,
      }));
    } catch (error) {
      alert(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * PDF generation
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const generatePDF = () => {
    const doc = new jsPDF();
    let y = 10;
    const pageH = doc.internal.pageSize.height;

    doc.setFontSize(18).setTextColor(0, 102, 204);
    doc.text(`Quiz: ${quizSource}`, 10, y);
    y += 12;

    quiz.forEach((q, i) => {
      doc.setFontSize(14).setTextColor(0, 0, 0);
      const questionLines = doc.splitTextToSize(`${i + 1}. ${q.question}`, 180);
      doc.text(questionLines, 10, y);
      y += questionLines.length * 7;

      if (q.options?.length) {
        doc.setFontSize(12).setTextColor(51, 51, 51);
        q.options.forEach((opt, j) => {
          const optLines = doc.splitTextToSize(`   ${String.fromCharCode(65 + j)}. ${opt}`, 175);
          doc.text(optLines, 15, y);
          y += optLines.length * 6;
        });
      }

      doc.setDrawColor(200, 200, 200).line(10, y, 200, y);
      y += 6;
      if (y > pageH - 20 && i < quiz.length - 1) {
        doc.addPage();
        y = 10;
      }
    });
    return doc.output("blob");
  };

  const exportToPDF = () => {
    const blob = generatePDF();
    setPdfBlob(blob);
    setPdfUrl(URL.createObjectURL(blob));
    setShowPdfModal(true);

    const quizDate = new Date().toLocaleDateString();
    setQuizHistory((prev) => [
      ...prev,
      {
        title: `Quiz ${prev.length + 1} - ${quizDate}`,
        questions: [...quiz],
        dateCreated: quizDate,
        type: quizType,
        source: quizSource,
        pdf: blob,
      },
    ]);

    setUserProfile((prev) => ({
      ...prev,
      downloads: (prev.downloads || 0) + 1,
      lastActive: new Date().toLocaleDateString(),
    }));
  };

  const viewPDF = (blob) => {
    const url = URL.createObjectURL(blob);
    window.open(url, "_blank");
  };

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Quiz‚Äëtaking helpers
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const handleSubmitQuiz = () => {
    setTimerActive(false);
    const total = quiz.length;
    let correct = 0;
    quiz.forEach((q, idx) => {
      if ((userAnswers[idx] || "").toString().trim().toLowerCase() === (q.answer || "").toString().trim().toLowerCase()) {
        correct += 1;
      }
    });
    setScore({ total, correct });
    setShowResults(true);
  };

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Effects ‚Äì timer & reset on preview enter
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  useEffect(() => {
    if (step === "preview") {
      setTimeLeft(60 * 5); // 5 minutes default ‚Äì adjust if needed
      setTimerActive(true);
      setUserAnswers({});
      setShowResults(false);
      setScore(null);
    }
  }, [step]);

  useEffect(() => {
    if (timerActive && timeLeft > 0) {
      const id = setInterval(() => setTimeLeft((t) => t - 1), 1000);
      return () => clearInterval(id);
    }
    if (timerActive && timeLeft === 0) {
      handleSubmitQuiz();
    }
  }, [timerActive, timeLeft]);

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * Render helpers ‚Äì question block
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  const renderQuestion = (q, idx) => (
    <div key={idx} className="question-card">
      <div className="question-header">
        <span className="question-number">Question {idx + 1}</span>
        <span className="question-type">
          {quizType === "video" ? "Video" : quizType === "url" ? "Web" : quizType === "tf" ? "True/False" : quizType === "fib" ? "Fill‚Äëin" : "Multiple Choice"}
        </span>
      </div>
      <div className="question-content">
        <p className="question-text">{q.question}</p>

        {q.options && q.options.length > 0 ? (
          <div className="options-grid">
            {q.options.map((opt, i) => (
              <label key={i} className="option-item">
                <input
                  type="radio"
                  name={`q-${idx}`}
                  value={opt}
                  checked={userAnswers[idx] === opt}
                  onChange={() => setUserAnswers((prev) => ({ ...prev, [idx]: opt }))}
                />
                <span>{opt}</span>
              </label>
            ))}
          </div>
        ) : (
          <input
            type="text"
            className="fib-input"
            placeholder="Your answer"
            value={userAnswers[idx] || ""}
            onChange={(e) => setUserAnswers((prev) => ({ ...prev, [idx]: e.target.value }))}
          />
        )}
      </div>
    </div>
  );

  /* ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
   * JSX
   * ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ*/
  return (
    <div className="app-container">
      {/* Header */}
      {step !== "auth" && (
        <header className="app-header">
          <h1>{getCurrentPageTitle()}</h1>
          <button className="profile-button" onClick={() => setShowProfile(!showProfile)}>
            üë§ {userProfile?.username || "Profile"}
            {userProfile?.quizzesGenerated > 0 && <span className="notification-badge">{userProfile.quizzesGenerated}</span>}
          </button>
        </header>
      )}

      {/* Profile modal (unchanged from original) */}
      {showProfile && userProfile && (
        <div className="profile-modal">
          {/*  ‚Ä¶ existing profile modal content kept unchanged ‚Ä¶ */}
        </div>
      )}

      {/* Main content */}
      <main className="app-content">
        {/* AUTH STEP */}
        {step === "auth" && (
          <div className="auth-container">
            {/* ‚Ä¶ existing auth UI ‚Ä¶ */}
          </div>
        )}

        {/* UPLOAD STEP */}
        {step === "upload" && (
          <div className="upload-container">
            {/* ‚Ä¶ existing upload UI (unchanged) ‚Ä¶ */}
          </div>
        )}

        {/* PREVIEW / QUIZ STEP */}
        {step === "preview" && (
          <div className="preview-container">
            <div className="preview-card">
              <div className="quiz-header">
                <h2>Quiz</h2>
                <div className="timer-box">
                  ‚è±Ô∏è Time Left: {Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")}
                </div>
                <div className="quiz-source">Source: <span>{quizSource}</span></div>
              </div>

              {/* QUESTIONS */}
              <div className="quiz-questions">
                {quiz.map(renderQuestion)}
              </div>

              {/* ACTIONS/RESULTS */}
              <div className="preview-actions">
                {!showResults ? (
                  <button className="submit-btn" onClick={handleSubmitQuiz}>Submit Quiz</button>
                ) : (
                  <div className="results-summary">
                    <h3>Score Summary</h3>
                    <p>Total Questions: {score.total}</p>
                    <p>Correct Answers: {score.correct}</p>
                    <p>Percentage: {(score.correct / score.total * 100).toFixed(2)}%</p>
                    <hr />
                    <h4>Correct Answers</h4>
                    {quiz.map((q, i) => (
                      <p key={i}><strong>{i + 1}.</strong> {q.question} ‚Äì <em>{q.answer}</em></p>
                    ))}

                    <button className="export-btn" onClick={exportToPDF}>Export to PDF</button>
                    <button className="another-btn" onClick={() => setStep("upload")}>Create Another Quiz</button>
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </main>

      {/* PDF MODAL */}
      {showPdfModal && (
        <div className="pdf-modal">
          {/* ‚Ä¶ existing pdf modal content unchanged ‚Ä¶ */}
        </div>
      )}
    </div>
  );
}
