import React, { useState, useEffect } from "react";
import { jsPDF } from "jspdf";
import "./App.css";

const quizTypes = [
  { label: "Multiple Choice", value: "mcq" },
  { label: "True / False", value: "tf" },
  { label: "Fill in the Blanks", value: "fib" },
  { label: "Video Based", value: "video" },
  { label: "URL Based", value: "url" },
];

export default function App() {
  const [step, setStep] = useState("auth");
  const [authStep, setAuthStep] = useState("login");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [quizType, setQuizType] = useState("mcq");
  const [quiz, setQuiz] = useState([]);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  const [url, setUrl] = useState("");
  const [userProfile, setUserProfile] = useState(null);
  const [quizSource, setQuizSource] = useState("");
  const [quizHistory, setQuizHistory] = useState([]);
  const [pdfBlob, setPdfBlob] = useState(null);
  const [showPdfModal, setShowPdfModal] = useState(false);
  const [pdfUrl, setPdfUrl] = useState("");


  const isValidUrl = (urlString) => {
    try {
      new URL(urlString);
      return true;
    } catch (e) {
      return false;
    }
  };

  const updateProfileStats = () => {
    if (userProfile) {
      setUserProfile({
        ...userProfile,
        quizzesGenerated: quizHistory.length,
        lastActive: new Date().toLocaleDateString(),
      });
    }
  };

  const handleRegister = async () => {
    try {
      const res = await fetch("http://localhost:8000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      const data = await res.json();
      if (res.ok) {
        alert("Registration successful! Please login.");
        setAuthStep("login");
      } else {
        alert(data.message || "Registration failed");
      }
    } catch (error) {
      alert("Registration error: " + error.message);
    }
  };

  const handleLogin = async () => {
    try {
      const formData = new URLSearchParams();
      formData.append("username", username);
      formData.append("password", password);

      const res = await fetch("http://localhost:8000/login", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();

      if (res.ok) {
        const newProfile = {
          username,
          email: `${username}@example.com`,
          joinedDate: new Date().toISOString().split('T')[0],
          quizzesGenerated: 0,
          downloads: 0,
          lastActive: new Date().toLocaleDateString(),
        };
        setUserProfile(newProfile);
        setStep("upload");
      } else {
        alert(data.message || "Login failed");
      }
    } catch (error) {
      alert("Login error: " + error.message);
    }
  };

  const handleUpload = async () => {
    if ((quizType !== "url" && !file) || (quizType === "url" && !url)) {
      alert("Please provide the required input");
      return;
    }

    if (quizType === "url" && !isValidUrl(url)) {
      alert("Please enter a valid URL");
      return;
    }

    setLoading(true);

    const formData = new FormData();
    if (file) formData.append("file", file);
    formData.append("quiz_type", quizType);
    if (url) formData.append("url", url);

    try {
      const res = await fetch("http://localhost:8000/generate-quiz", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.detail || "Failed to generate quiz");
      }

      const data = await res.json();
      setQuiz(data.quiz);
      setQuizSource(quizType === "url" ? url : file ? file.name : "content");
      setStep("preview");

      // Update quiz generation count
      setUserProfile(prev => ({
        ...prev,
        quizzesGenerated: (prev.quizzesGenerated || 0) + 1
      }));
    } catch (error) {
      alert(`Error: ${error.message}`);
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

const generatePDF = () => {
  const doc = new jsPDF();

  // Custom font settings (if you've added custom fonts)
  doc.setFont("helvetica", "normal");

  // ===== COVER PAGE =====
  // Gradient background (simulated with rectangles)
  const coverGradient = [
    { color: "#4361ee", y: 0, height: 70 },
    { color: "#3a0ca3", y: 70, height: doc.internal.pageSize.height - 70 }
  ];
  coverGradient.forEach(part => {
    doc.setFillColor(part.color);
    doc.rect(0, part.y, doc.internal.pageSize.width, part.height, 'F');
  });

  // Title with shadow effect
  doc.setTextColor("#ffffff");
  doc.setFontSize(32);
  doc.setFont("helvetica", "bold");
  doc.text("‚ú® Quiz Master ‚ú®", 105, 50, { align: "center" });

  // Decorative underline
  doc.setDrawColor("#ffffff");
  doc.setLineWidth(1.5);
  doc.line(60, 60, 150, 60);

  // Source information
  doc.setFontSize(16);
  doc.text(`Source: ${quizSource}`, 105, 80, { align: "center" });

  // Quiz info card with glass morphism effect
  doc.setFillColor(255, 255, 255, 0.15);
  doc.roundedRect(40, 100, 130, 80, 10, 10, 'F');
  doc.setDrawColor(255, 255, 255, 0.3);
  doc.setLineWidth(0.5);
  doc.roundedRect(40, 100, 130, 80, 10, 10, 'S');

  // Quiz details
  doc.setTextColor("#ffffff");
  doc.setFontSize(14);
  doc.text(`üìù ${quiz.length} Questions`, 105, 115, { align: "center" });
  doc.text(`üìÖ ${new Date().toLocaleDateString()}`, 105, 130, { align: "center" });
  doc.text(`üéØ ${quizTypes.find(t => t.value === quizType)?.label || 'Quiz'}`, 105, 145, { align: "center" });

  // Decorative elements
  doc.setFontSize(48);
  doc.text("üß†", 105, 180, { align: "center" });

  // ===== QUESTIONS PAGES =====
  doc.addPage();
  let yPosition = 30;

  // Color palette
  const questionColors = [
    "#4361ee", "#3f37c9", "#4895ef", "#4cc9f0", 
    "#7209b7", "#b5179e", "#f72585", "#560bad"
  ];

  quiz.forEach((q, i) => {
    // Add new page after every 2 questions (for better spacing)
    if (i > 0 && i % 2 === 0) {
      doc.addPage();
      yPosition = 30;
    }

    const pageWidth = doc.internal.pageSize.width;
    const color = questionColors[i % questionColors.length];

    // === QUESTION HEADER ===
    // Colorful header with question number
    doc.setFillColor(color);
    doc.roundedRect(15, yPosition, pageWidth - 30, 12, 4, 4, 'F');

    // Question number with icon
    doc.setTextColor("#ffffff");
    doc.setFontSize(12);
    doc.setFont("helvetica", "bold");
    doc.text(`‚ùì Question ${i + 1}`, 20, yPosition + 8);

    yPosition += 15;

    // === QUESTION TEXT ===
    doc.setTextColor("#333333");
    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");

    // Split long questions into multiple lines
    const questionLines = doc.splitTextToSize(q.question, pageWidth - 40);
    questionLines.forEach(line => {
      doc.text(line, 20, yPosition);
      yPosition += 7;
    });

    yPosition += 5;

    // === OPTIONS ===
    if (q.options && q.options.length > 0) {
      q.options.forEach((opt, j) => {
        // Option container
        doc.setFillColor("#f8f9fa");
        doc.roundedRect(20, yPosition, pageWidth - 40, 8, 2, 2, 'F');

        // Option letter with color
        doc.setFillColor(color);
        doc.circle(23, yPosition + 4, 3, 'F');

        // Option text
        doc.setTextColor("#333333");
        doc.text(`${String.fromCharCode(65 + j)}. ${opt}`, 30, yPosition + 5);

        yPosition += 10;
      });
    }

    // === CORRECT ANSWER ===
    yPosition += 8;

    // Highlighted answer section
    doc.setFillColor("#4bb543"); // Green for correct answer
    doc.roundedRect(15, yPosition, pageWidth - 30, 10, 4, 4, 'F');

    // Answer text with icon and custom font
    doc.setTextColor("#ffffff");
    doc.setFontSize(11);
    doc.setFont("helvetica", "bold");

    const answerText = `‚úÖ Correct Answer: ${q.answer}`;
    const answerWidth = doc.getTextWidth(answerText);
    doc.text(answerText, 20, yPosition + 7);

    // Decorative star for important answers
    if (i % 3 === 0) { // Every 3rd question gets special treatment
      doc.setFontSize(8);
      doc.text("‚≠ê", 20 + answerWidth + 5, yPosition + 7);
    }

    yPosition += 15;

    // === QUESTION SEPARATOR ===
    if (i < quiz.length - 1) {
      doc.setDrawColor("#eeeeee");
      doc.setLineWidth(0.5);
      doc.line(15, yPosition, pageWidth - 15, yPosition);
      doc.setFillColor(color);
      doc.circle(105, yPosition, 2, 'F');
      yPosition += 15;
    }

    // Page break check
    if (yPosition > 240) {
      doc.addPage();
      yPosition = 30;
    }
  });

  // ===== FINAL TOUCHES =====
  const pageCount = doc.internal.getNumberOfPages();

  // Add footer to all pages
  for (let i = 1; i <= pageCount; i++) {
    doc.setPage(i);

    // Page number
    doc.setFontSize(10);
    doc.setTextColor("#999999");
    doc.text(`Page ${i} of ${pageCount}`, doc.internal.pageSize.width - 25, doc.internal.pageSize.height - 10);

    // Decorative border elements
    doc.setDrawColor("#4361ee");
    doc.setLineWidth(0.3);

    // Top-left corner
    doc.line(10, 10, 20, 10);
    doc.line(10, 10, 10, 20);

    // Top-right corner
    doc.line(doc.internal.pageSize.width - 10, 10, doc.internal.pageSize.width - 20, 10);
    doc.line(doc.internal.pageSize.width - 10, 10, doc.internal.pageSize.width - 10, 20);

    // Bottom-left corner
    doc.line(10, doc.internal.pageSize.height - 10, 20, doc.internal.pageSize.height - 10);
    doc.line(10, doc.internal.pageSize.height - 10, 10, doc.internal.pageSize.height - 20);

    // Bottom-right corner
    doc.line(doc.internal.pageSize.width - 10, doc.internal.pageSize.height - 10, 
             doc.internal.pageSize.width - 20, doc.internal.pageSize.height - 10);
    doc.line(doc.internal.pageSize.width - 10, doc.internal.pageSize.height - 10, 
             doc.internal.pageSize.width - 10, doc.internal.pageSize.height - 20);

    // Add watermark to content pages (not cover)
    if (i > 1) {
      doc.setFontSize(40);
      doc.setTextColor(67, 97, 238, 0.05); // Very light primary color
      doc.setFont("helvetica", "bold");
      doc.text("QUIZ MASTER", 105, 150, { align: "center", angle: 45 });
    }
  }

  return doc.output('blob');
};

const exportToPDF = () => {
  const pdfBlob = generatePDF();
  setPdfBlob(pdfBlob);

  // Create URL for preview
  const url = URL.createObjectURL(pdfBlob);
  setPdfUrl(url);
  setShowPdfModal(true);

  // Update profile and history
  const quizDate = new Date().toLocaleDateString();
  const newQuiz = { 
    title: `Quiz ${quizHistory.length + 1} - ${quizDate}`,
    questions: [...quiz],
    dateCreated: quizDate,
    type: quizType,
    source: quizSource,
    pdf: pdfBlob
  };

  setQuizHistory([...quizHistory, newQuiz]);
  setUserProfile(prev => ({
    ...prev,
    downloads: (prev.downloads || 0) + 1,
    lastActive: new Date().toLocaleDateString()
  }));
};


  const viewPDF = (pdfBlob) => {
    const url = URL.createObjectURL(pdfBlob);
    window.open(url, '_blank');
  };

  const handleCreateAnother = () => {
    setQuiz([]);
    setFile(null);
    setUrl("");
    setSuccess(false);
    setStep("upload");
  };

  const handleLogout = () => {
    setStep("auth");
    setUsername("");
    setPassword("");
    setUserProfile(null);
    setShowProfile(false);
  };

  const getCurrentPageTitle = () => {
    switch(step) {
      case "upload": return "Create New Quiz";
      case "preview": return "Quiz Preview";
      case "auth": return authStep === "login" ? "Login" : "Register";
      default: return "AI Quiz Generator";
    }
  };

  return (
    <div className="app-container">
      {/* Header */}
      {step !== "auth" && (
        <header className="app-header">
          <h1>{getCurrentPageTitle()}</h1>
          <button 
            className="profile-button" 
            onClick={() => setShowProfile(!showProfile)}
          >
            üë§ {userProfile?.username || "Profile"}
            {userProfile?.quizzesGenerated > 0 && (
              <span className="notification-badge">{userProfile.quizzesGenerated}</span>
            )}
          </button>
        </header>
      )}

      {/* Profile Modal */}
      {showProfile && userProfile && (
        <div className="profile-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h2>User Profile</h2>
              <button className="close-btn" onClick={() => setShowProfile(false)}>√ó</button>
            </div>

            <div className="profile-section">
              <div className="profile-info">
                <div className="info-item">
                  <span className="info-label">Username:</span>
                  <span className="info-value">{userProfile.username}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Email:</span>
                  <span className="info-value">{userProfile.email}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Member since:</span>
                  <span className="info-value">{userProfile.joinedDate}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Last active:</span>
                  <span className="info-value">{userProfile.lastActive}</span>
                </div>
              </div>

              <div className="stats-section">
                <div className="stat-card">
                  <div className="stat-value">{userProfile.quizzesGenerated || 0}</div>
                  <div className="stat-label">Quizzes Generated</div>
                </div>
                <div className="stat-card">
                  <div className="stat-value">{userProfile.downloads || 0}</div>
                  <div className="stat-label">PDF Downloads</div>
                </div>
              </div>
            </div>

            <div className="quiz-history">
              <h3>Recent Quiz History</h3>
              {quizHistory.length === 0 ? (
                <p className="no-history">No quiz history yet</p>
              ) : (
                <ul className="history-list">
                  {quizHistory.slice(0, 5).map((quiz, index) => (
                    <li key={index} className="history-item">
                      <div className="quiz-title">{quiz.title}</div>
                      <div className="quiz-meta">
                        <span>Type: {quizTypes.find(t => t.value === quiz.type)?.label || 'Unknown'}</span>
                        <span>Date: {quiz.dateCreated}</span>
                      </div>
                      {quiz.pdf && (
                        <div className="quiz-actions">
                          <button 
                            className="view-pdf-btn"
                            onClick={() => viewPDF(quiz.pdf)}
                          >
                            View PDF
                          </button>
                          <button 
                            className="download-pdf-btn"
                            onClick={() => {
                              const url = URL.createObjectURL(quiz.pdf);
                              const a = document.createElement('a');
                              a.href = url;
                              a.download = `${quiz.title}.pdf`;
                              document.body.appendChild(a);
                              a.click();
                              document.body.removeChild(a);
                              URL.revokeObjectURL(url);

                              // Update download count
                              setUserProfile(prev => ({
                                ...prev,
                                downloads: (prev.downloads || 0) + 1
                              }));
                            }}
                          >
                            Download Again
                          </button>
                        </div>
                      )}
                    </li>
                  ))}
                </ul>
              )}
            </div>

            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
          <div className="modal-overlay" onClick={() => setShowProfile(false)} />
        </div>
      )}

      {/* Main Content */}
      <main className="app-content">
        {step === "auth" && (
          <div className="auth-container">
            <div className="auth-card">
              <h2>{authStep === "login" ? "Welcome Back" : "Create Account"}</h2>

              <div className="auth-form">
                <div className="form-group">
                  <label>Username</label>
                  <input
                    type="text"
                    placeholder="Enter your username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                  />
                </div>

                <div className="form-group">
                  <label>Password</label>
                  <input
                    type="password"
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>

                <button 
                  className="auth-btn"
                  onClick={authStep === "login" ? handleLogin : handleRegister}
                >
                  {authStep === "login" ? "Login" : "Register"}
                </button>

                <div className="auth-switch">
                  {authStep === "login" ? (
                    <p>Don't have an account? <span onClick={() => setAuthStep("register")}>Register</span></p>
                  ) : (
                    <p>Already have an account? <span onClick={() => setAuthStep("login")}>Login</span></p>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {step === "upload" && (
          <div className="upload-container">
            <div className="upload-card">
              <h2>Create New Quiz</h2>
              <p className="subtitle">Select content source and quiz type</p>

              <div className="quiz-type-selector">
                <label>Quiz Type</label>
                <div className="type-options">
                  {quizTypes.map((type) => (
                    <div 
                      key={type.value}
                      className={`type-option ${quizType === type.value ? 'active' : ''}`}
                      onClick={() => {
                        setQuizType(type.value);
                        setFile(null);
                        setUrl("");
                      }}
                    >
                      {type.label}
                    </div>
                  ))}
                </div>
              </div>

              <div className="source-input">
                {quizType === "url" ? (
                  <div className="url-input">
                    <label>Enter URL</label>
                    <input
                      type="url"
                      placeholder="https://example.com"
                      value={url}
                      onChange={(e) => setUrl(e.target.value)}
                    />
                    <p className="hint">Supports articles, blogs, and educational content</p>
                  </div>
                ) : (
                  <div className="file-upload-area">
                    <label>
                      {quizType === "video" ? "Upload Video" : "Upload Document"}
                    </label>
                    <div className={`upload-box ${file ? 'has-file' : ''}`}>
                      {file ? (
                        <>
                          <div className="file-info">
                            <span className="file-name">{file.name}</span>
                            <span className="file-size">
                              {(file.size / (1024 * 1024)).toFixed(2)} MB
                            </span>

                          <button 
                            className="clear-file"
                            onClick={() => setFile(null)}
                          >
                            √ó
                          </button>
                          </div>
                        </>
                      ) : (
                        <>
                          <input
                            type="file"
                            accept={
                              quizType === "video" 
                                ? "video/*,.mp4,.webm,.mov" 
                                : ".pdf,.docx,.txt"
                            }
                            onChange={(e) => setFile(e.target.files[0])}
                          />
                          <div className="upload-prompt">
                            <span className="upload-icon">‚Üë</span>
                            <p>Drag & drop or click to browse</p>
                            <p className="file-types">
                              {quizType === "video" 
                                ? "MP4, WebM, MOV" 
                                : "PDF, DOCX, TXT"}
                            </p>
                          </div>
                        </>
                      )}
                    </div>
                  </div>
                )}
              </div>

              <button 
                className={`generate-btn ${loading ? 'loading' : ''}`} 
                onClick={handleUpload} 
                disabled={
                  loading || 
                  (quizType === "url" ? !url : !file) ||
                  (quizType === "url" && !isValidUrl(url))
                }
              >
                {loading ? (
                  <>
                    <span className="spinner"></span>
                    {quizType === "video" ? "Processing Video..." : 
                     quizType === "url" ? "Analyzing URL..." : "Generating Quiz..."}
                  </>
                ) : (
                  "Generate Quiz"
                )}
              </button>
            </div>
          </div>
        )}

        {step === "preview" && (
          <div className="preview-container">
            <div className="preview-card">
              <div className="quiz-header">
                <h2>Quiz Preview</h2>
                <div className="quiz-source">
                  Generated from: <span>{quizSource}</span>
                </div>
              </div>

              <div className="quiz-questions">
                {quiz.map((q, idx) => (
                  <div key={idx} className="question-card">
                    <div className="question-header">
                      <span className="question-number">Question {idx + 1}</span>
                      <span className="question-type">
                        {
                          quizType === 'video' ? 'Video Question' :
                          quizType === 'url' ? 'Web Content Question' :
                          quizType === 'tf' ? 'True/False' :
                          quizType === 'fib' ? 'Fill in Blank' :
                          q.options && q.options.length > 2 ? 'Multiple Choice' :
                          'Short Answer'
                        }
                      </span>
                    </div>

                    <div className="question-content">
                      <input
                        type="text"
                        value={q.question}
                        onChange={(e) => {
                          const newQuiz = [...quiz];
                          newQuiz[idx].question = e.target.value;
                          setQuiz(newQuiz);
                        }}
                        placeholder="Enter question"
                      />

                      {q.options && q.options.length > 0 && (
                        <div className="options-grid">
                          {q.options.map((opt, i) => (
                            <div key={i} className="option-item">
                              <span className="option-letter">{String.fromCharCode(65 + i)}</span>
                              <input
                                type="text"
                                value={opt}
                                onChange={(e) => {
                                  const newQuiz = [...quiz];
                                  newQuiz[idx].options[i] = e.target.value;
                                  setQuiz(newQuiz);
                                }}
                                placeholder={`Option ${i + 1}`}
                              />
                            </div>
                          ))}
                        </div>
                      )}

                      <div className="correct-answer">
                        <label>Correct Answer:</label>
                        {q.options && q.options.length > 0 ? (
                          <select
                            value={q.answer}
                            onChange={(e) => {
                              const newQuiz = [...quiz];
                              newQuiz[idx].answer = e.target.value;
                              setQuiz(newQuiz);
                            }}
                          >
                            {q.options.map((opt, i) => (
                              <option key={i} value={opt}>
                                {String.fromCharCode(65 + i)}. {opt}
                              </option>
                            ))}
                          </select>
                        ) : (
                          <input
                            type="text"
                            value={q.answer}
                            onChange={(e) => {
                              const newQuiz = [...quiz];
                              newQuiz[idx].answer = e.target.value;
                              setQuiz(newQuiz);
                            }}
                            placeholder="Enter correct answer"
                          />
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              <div className="preview-actions">
                {!success && (
                  <>
                    <button className="export-btn" onClick={exportToPDF}>
                      Export to PDF
                    </button>
                    <button className="another-btn" onClick={handleCreateAnother}>
                      Create Another Quiz
                    </button>
                  </>
                )}

                {success && (
                  <div className="success-message">
                    <div className="success-icon">‚úì</div>
                    <h3>Quiz exported successfully!</h3>
                    <p>Your quiz has been saved to your device and added to your history.</p>
                    <div className="success-actions">
                      <button className="view-pdf-btn" onClick={() => viewPDF(pdfBlob)}>
                        View PDF
                      </button>
                      <button className="home-btn" onClick={handleCreateAnother}>
                        Create Another Quiz
                      </button>
                    </div>
                  </div>
                )}




                {showPdfModal && (
  <div className="pdf-modal">
    <div className="pdf-modal-content">
      <div className="pdf-modal-header">
        <h3>Your Quiz is Ready!</h3>
        <button className="close-btn" onClick={() => setShowPdfModal(false)}>
          √ó
        </button>
      </div>

      <div className="pdf-preview-container">
        <embed 
          src={`${pdfUrl}#toolbar=0&navpanes=0`} 
          type="application/pdf" 
          className="pdf-preview"
        />
      </div>

      <div className="pdf-actions">
        <a 
          href={pdfUrl} 
          download={`quiz_${new Date().toISOString().slice(0, 10)}.pdf`}
          className="download-pdf-btn"
          onClick={() => {
            setTimeout(() => {
              setShowPdfModal(false);
              setSuccess(true);
            }, 500);
          }}
        >
          <span className="icon">‚Üì</span> Download PDF
        </a>

        <button 
          className="share-pdf-btn"
          onClick={() => {
            if (navigator.share) {
              navigator.share({
                title: 'Generated Quiz',
                text: 'Check out this quiz I generated!',
                url: pdfUrl,
              }).catch(console.error);
            } else {
              alert('Web Share API not supported in your browser');
            }
          }}
        >
          <span className="icon">‚Üó</span> Share
        </button>

        <button 
          className="view-later-btn"
          onClick={() => {
            setShowPdfModal(false);
            setSuccess(true);
          }}
        >
          <span className="icon">üëÅÔ∏è</span> View Later
        </button>
      </div>

      <div className="pdf-stats">
        <div className="stat-item">
          <span className="stat-icon">üìä</span>
          <span>{quiz.length} Questions</span>
        </div>
        <div className="stat-item">
          <span className="stat-icon">‚è±Ô∏è</span>
          <span>Generated just now</span>
        </div>
      </div>
    </div>
    <div className="pdf-modal-overlay" onClick={() => setShowPdfModal(false)} />
  </div>
)}
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
