import React, { useState } from 'react';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [currentUser, setCurrentUser] = useState('');
  const [quizType, setQuizType] = useState('mcq');
  const [file, setFile] = useState(null);
  const [url, setUrl] = useState('');
  const [quizOutput, setQuizOutput] = useState('');

  const register = () => {
    fetch('/register', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({ username, password })
    })
    .then(res => res.json())
    .then(data => alert(data.message))
    .catch(() => alert("Register failed"));
  };

  const login = () => {
    const formData = new URLSearchParams();
    formData.append('username', username);
    formData.append('password', password);

    fetch('/login', {
      method: 'POST',
      headers: {'Content-Type': 'application/x-www-form-urlencoded'},
      body: formData
    })
    .then(res => {
      if (!res.ok) throw new Error('Login failed');
      return res.json();
    })
    .then(data => setCurrentUser(data.username))
    .catch(() => alert('Login failed'));
  };

  const generateQuiz = () => {
    const formData = new FormData();
    formData.append('quiz_type', quizType);

    if (quizType === 'url') {
      formData.append('url', url);
    } else {
      if (!file) {
        alert('Please upload a file.');
        return;
      }
      formData.append('file', file);
    }

    fetch('/generate-quiz', {
      method: 'POST',
      body: formData
    })
    .then(res => res.json())
    .then(data => setQuizOutput(JSON.stringify(data.quiz, null, 2)))
    .catch(() => alert('Quiz generation failed'));
  };

  return (
    <div className="container">
      <h1>AI Quiz Creator</h1>

      {!currentUser ? (
        <div id="auth-section">
          <h2>Login / Register</h2>
          <input placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
          <button onClick={register}>Register</button>
          <button onClick={login}>Login</button>
        </div>
      ) : (
        <div id="quiz-section">
          <h2>Generate Quiz</h2>

          <label>Select Quiz Type:</label>
          <select value={quizType} onChange={e => setQuizType(e.target.value)}>
            <option value="mcq">Multiple Choice</option>
            <option value="tf">True / False</option>
            <option value="fib">Fill in the Blanks</option>
            <option value="video">Video Upload</option>
            <option value="url">URL</option>
          </select>

          {quizType === 'url' ? (
            <input placeholder="Enter URL" value={url} onChange={e => setUrl(e.target.value)} />
          ) : (
            <input type="file" onChange={e => setFile(e.target.files[0])} />
          )}

          <button onClick={generateQuiz}>Generate Quiz</button>
          <pre>{quizOutput}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
