import React, { useState } from "react";
import { jsPDF } from "jspdf";

const quizTypes = [
  { label: "Multiple Choice", value: "mcq" },
  { label: "True / False", value: "tf" },
  { label: "Fill in the Blanks", value: "fib" },
];

export default function App() {
  const [step, setStep] = useState("home");
  const [file, setFile] = useState(null);
  const [quizType, setQuizType] = useState("mcq");
  const [quiz, setQuiz] = useState([]);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleStart = () => setStep("upload");
const [authStep, setAuthStep] = useState("login"); // or "register"
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");




const handleRegister = async () => {
  const res = await fetch("http://localhost:8000/register", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ username, password }),
  });
  const data = await res.json();
  alert(data.message);
  if (res.ok) setStep("home");
};

const handleLogin = async () => {
  const formData = new URLSearchParams();
  formData.append("username", username);
  formData.append("password", password);

  const res = await fetch("http://localhost:8000/login", {
    method: "POST",
    body: formData,
  });
  const data = await res.json();
  alert(data.message);
  if (res.ok) setStep("home");
};



const handleUpload = async () => {
  if (!file) {
    alert("Please select a file.");
    return;
  }
  setLoading(true);

  const formData = new FormData();
  formData.append("file", file);
  formData.append("quiz_type", quizType);

  try {
    const res = await fetch("http://localhost:8000/generate-quiz", {
      method: "POST",
      body: formData,
    });

    if (!res.ok) {
      const errorData = await res.json();
      throw new Error(errorData.detail || "Failed to generate quiz");
    }

    const data = await res.json();
    setQuiz(data.quiz);
    setStep("preview");
  } catch (error) {
    alert(`Error: ${error.message}\n\nPlease check:\n1. Is the backend running?\n2. Did you upload a valid file?\n3. Is your OpenAI API key set?`);
    console.error("Fetch error:", error);
  } finally {
    setLoading(false);
  }
};

  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPosition = 10;

    quiz.forEach((q, i) => {
      // Add question
      doc.text(`${i + 1}. ${q.question}`, 10, yPosition);
      yPosition += 10;

      // Add options if they exist
      if (q.options && q.options.length > 0) {
        q.options.forEach((opt, j) => {
          doc.text(`   ${String.fromCharCode(65 + j)}. ${opt}`, 15, yPosition);
          yPosition += 7;
        });
      }

      // Add correct answer
      // doc.text(`Correct Answer: ${q.answer}`, 15, yPosition);
      // yPosition += 15;

      // Add page break if needed
      if (yPosition > 280 && i < quiz.length - 1) {
        doc.addPage();
        yPosition = 10;
      }
    });

    doc.save("quiz.pdf");
    setSuccess(true);
  };

  const handleCreateAnother = () => {
    setQuiz([]);
    setFile(null);
    setSuccess(false);
    setStep("upload");
  };

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "Arial" }}>
      {step === "home" && (
  <>
    <h1>AI-Powered Quiz Creator</h1>
    <p>Upload your lecture notes or textbook to instantly generate a quiz!</p>
    <button onClick={handleStart}>Start Now</button>
  </>
)}

{step === "auth" && (
  <>
    <h2>{authStep === "login" ? "Login" : "Register"}</h2>
    <input
      type="text"
      placeholder="Username"
      value={username}
      onChange={(e) => setUsername(e.target.value)}
    />
    <input
      type="password"
      placeholder="Password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    <button onClick={authStep === "login" ? handleLogin : handleRegister}>
      {authStep === "login" ? "Login" : "Register"}
    </button>
    <button onClick={() => setAuthStep(authStep === "login" ? "register" : "login")}>
      Switch to {authStep === "login" ? "Register" : "Login"}
    </button>
  </>
)}


      {step === "upload" && (
        <>
          <h2>Upload Notes</h2>
          <input
            type="file"
            accept=".pdf,.docx,.txt"
            onChange={(e) => setFile(e.target.files[0])}
          />
          <div style={{ margin: "1rem 0" }}>
            <label>
              Quiz Type:{" "}
              <select
                value={quizType}
                onChange={(e) => setQuizType(e.target.value)}
              >
                {quizTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </label>
          </div>
          <button onClick={handleUpload} disabled={loading}>
            {loading ? "Generating Quiz..." : "Upload & Generate Quiz"}
          </button>
          <button onClick={() => setStep("home")} style={{ marginLeft: 10 }}>
            Cancel
          </button>
        </>
      )}

      {step === "preview" && (
        <>
          <h2>Quiz Preview</h2>
          {quiz.map((q, idx) => (
            <div
              key={idx}
              style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}
            >
              <p>
                <strong>Question {idx + 1}:</strong>{" "}
                <input
                  type="text"
                  value={q.question}
                  onChange={(e) => {
                    const newQuiz = [...quiz];
                    newQuiz[idx].question = e.target.value;
                    setQuiz(newQuiz);
                  }}
                  style={{ width: "100%" }}
                />
              </p>

              {q.options && q.options.length > 0 && (
                <ul>
                  {q.options.map((opt, i) => (
                    <li key={i}>
                      <input
                        type="text"
                        value={opt}
                        onChange={(e) => {
                          const newQuiz = [...quiz];
                          newQuiz[idx].options[i] = e.target.value;
                          setQuiz(newQuiz);
                        }}
                      />
                    </li>
                  ))}
                </ul>
              )}

              <p>
                Correct Answer:{" "}
                {q.options && q.options.length > 0 ? (
                  <select
                    value={q.answer}
                    onChange={(e) => {
                      const newQuiz = [...quiz];
                      newQuiz[idx].answer = e.target.value;
                      setQuiz(newQuiz);
                    }}
                  >
                    {q.options.map((opt, i) => (
                      <option key={i} value={opt}>
                        {opt}
                      </option>
                    ))}
                  </select>
                ) : (
                  <input
                    type="text"
                    value={q.answer}
                    onChange={(e) => {
                      const newQuiz = [...quiz];
                      newQuiz[idx].answer = e.target.value;
                      setQuiz(newQuiz);
                    }}
                  />
                )}
              </p>
            </div>
          ))}

          {!success && (
            <>
              <button onClick={exportToPDF}>Export to PDF</button>
              <button onClick={handleCreateAnother} style={{ marginLeft: 10 }}>
                Create Another Quiz
              </button>
            </>
          )}

          {success && (
            <>
              <p>🎉 Your quiz has been exported as PDF!</p>
              <button onClick={() => setStep("home")}>Go to Homepage</button>
            </>
          )}
        </>
      )}
    </div>
  );
}
