import React, { useState, useRef } from 'react';
import { FiUpload, FiFile, FiUser, FiBook, FiHome, FiDownload } from 'react-icons/fi';
import { motion } from 'framer-motion';

function App() {
  // Authentication state
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [authForm, setAuthForm] = useState({
    email: '',
    password: '',
    name: ''
  });

  // App state
  const [files, setFiles] = useState([]);
  const [activeTab, setActiveTab] = useState('home');
  const fileInputRef = useRef(null);

  // Handle auth form changes
  const handleAuthChange = (e) => {
    const { name, value } = e.target;
    setAuthForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle login/register
  const handleAuthSubmit = (e) => {
    e.preventDefault();
    // In a real app, you would validate and send to backend here
    setIsLoggedIn(true);
  };

  // Handle file upload
  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(prev => [...prev, ...selectedFiles]);
  };

  const triggerFileInput = () => {
    fileInputRef.current.click();
  };

  // Remove file from list
  const removeFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  };

  // Download file (mock function)
  const downloadFile = (file) => {
    alert(`Downloading ${file.name}`);
    // Actual implementation would create a download link
  };

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 px-4">
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="max-w-md w-full mx-auto bg-white rounded-xl shadow-md overflow-hidden p-6"
        >
          <div className="flex justify-center mb-6">
            <h1 className="text-2xl font-bold text-gray-800">AI Quiz Generator</h1>
          </div>

          <div className="flex border-b mb-6">
            <button
              className={`flex-1 py-2 font-medium ${showLogin ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
              onClick={() => setShowLogin(true)}
            >
              Login
            </button>
            <button
              className={`flex-1 py-2 font-medium ${!showLogin ? 'text-blue-600 border-b-2 border-blue-600' : 'text-gray-500'}`}
              onClick={() => setShowLogin(false)}
            >
              Register
            </button>
          </div>

          <form onSubmit={handleAuthSubmit} className="space-y-4">
            {!showLogin && (
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
                <input
                  type="text"
                  name="name"
                  value={authForm.name}
                  onChange={handleAuthChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
            )}

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
              <input
                type="email"
                name="email"
                value={authForm.email}
                onChange={handleAuthChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
              <input
                type="password"
                name="password"
                value={authForm.password}
                onChange={handleAuthChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
                minLength="6"
              />
            </div>

            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
            >
              {showLogin ? 'Login' : 'Register'}
            </button>
          </form>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* Mobile header */}
      <header className="bg-white shadow-sm py-3 px-4 flex justify-between items-center">
        <h1 className="text-xl font-bold text-gray-800">AI Quiz Generator</h1>
        <button className="p-2 rounded-full bg-gray-100 text-gray-600">
          <FiUser className="text-lg" />
        </button>
      </header>

      {/* Main content area */}
      <main className="flex-1 p-4">
        {activeTab === 'home' && (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="bg-white rounded-xl shadow-sm p-4"
          >
            <h2 className="text-lg font-semibold mb-4">Upload Documents</h2>

            <div 
              onClick={triggerFileInput}
              className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center cursor-pointer hover:border-blue-500 transition-colors mb-4"
            >
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileChange}
                className="hidden"
                accept=".pdf,.docx,.txt"
                multiple
              />
              <FiUpload className="mx-auto text-3xl text-gray-400 mb-2" />
              <p className="text-gray-600">Click to upload files</p>
              <p className="text-sm text-gray-400 mt-1">PDF, DOCX, or TXT files</p>
            </div>

            {files.length > 0 && (
              <div className="space-y-3">
                <h3 className="font-medium text-gray-700">Selected Files</h3>
                {files.map((file, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center">
                      <FiFile className="text-gray-500 mr-2" />
                      <span className="text-sm text-gray-700 truncate max-w-[180px]">{file.name}</span>
                    </div>
                    <div className="flex space-x-2">
                      <button 
                        onClick={() => downloadFile(file)}
                        className="text-blue-500 hover:text-blue-700"
                      >
                        <FiDownload />
                      </button>
                      <button 
                        onClick={() => removeFile(index)}
                        className="text-red-500 hover:text-red-700"
                      >
                        Ã—
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </motion.div>
        )}

        {activeTab === 'library' && (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="bg-white rounded-xl shadow-sm p-4"
          >
            <h2 className="text-lg font-semibold mb-4">Your Library</h2>
            <div className="text-center py-8 text-gray-500">
              <FiBook className="mx-auto text-4xl mb-2" />
              <p>Your generated quizzes will appear here</p>
            </div>
          </motion.div>
        )}
      </main>

      {/* Mobile bottom navigation */}
      <nav className="bg-white border-t flex justify-around py-2">
        <button 
          onClick={() => setActiveTab('home')}
          className={`flex flex-col items-center p-2 ${activeTab === 'home' ? 'text-blue-600' : 'text-gray-500'}`}
        >
          <FiHome className="text-lg" />
          <span className="text-xs mt-1">Home</span>
        </button>
        <button 
          onClick={() => setActiveTab('library')}
          className={`flex flex-col items-center p-2 ${activeTab === 'library' ? 'text-blue-600' : 'text-gray-500'}`}
        >
          <FiBook className="text-lg" />
          <span className="text-xs mt-1">Library</span>
        </button>
      </nav>
    </div>
  );
}

export default App;
