import React, { useState } from 'react';

const QuizGenerator = ({ onQuizGenerated }) => {
  const [quizType, setQuizType] = useState('mcq');
  const [file, setFile] = useState(null);
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGenerate = async () => {
    if (!file && !url) {
      alert('Please provide a file or URL.');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('quiz_type', quizType);
    if (file) formData.append('file', file);
    if (url) formData.append('url', url);

    try {
      const response = await fetch('http://localhost:8000/generate-quiz', {
        method: 'POST',
        body: formData,
      });
      const result = await response.json();
      if (response.ok) {
        onQuizGenerated(result.quiz);
      } else {
        alert(result.message);
      }
    } catch (error) {
      alert('An error occurred while generating the quiz.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="quiz-generator">
      <h2>Generate a Quiz</h2>
      <select value={quizType} onChange={(e) => setQuizType(e.target.value)}>
        <option value="mcq">Multiple Choice</option>
        <option value="tf">True / False</option>
        <option value="fib">Fill in the Blanks</option>
      </select>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <input type="url" value={url} onChange={(e) => setUrl(e.target.value)} placeholder="Or enter a URL" />
      <button onClick={handleGenerate} disabled={loading}>
        {loading ? 'Generating...' : 'Generate Quiz'}
      </button>
    </div>
  );
};

export default QuizGenerator;
