import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Typography, 
  Button, 
  Paper, 
  Radio, 
  RadioGroup, 
  FormControlLabel, 
  FormControl, 
  Chip
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import Confetti from 'react-confetti';

const QuizDisplay = ({ quizData }) => {
  const navigate = useNavigate();
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState('');
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [answers, setAnswers] = useState([]);
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });
  const [showConfetti, setShowConfetti] = useState(false);

  useEffect(() => {
    if (!quizData) {
      navigate('/generate');
    }
  }, [quizData, navigate]);

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleNext = () => {
    const isCorrect = selectedOption === quizData.quiz[currentQuestion].answer;
    
    setAnswers([...answers, {
      question: currentQuestion,
      selected: selectedOption,
      correct: isCorrect
    }]);

    if (isCorrect) {
      setScore(score + 1);
    }

    if (currentQuestion < quizData.quiz.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption('');
    } else {
      setShowResult(true);
      if (score + (isCorrect ? 1 : 0) === quizData.quiz.length) {
        setShowConfetti(true);
        setTimeout(() => setShowConfetti(false), 5000);
      }
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
      const prevAnswer = answers.find(a => a.question === currentQuestion - 1);
      setSelectedOption(prevAnswer ? prevAnswer.selected : '');
    }
  };

  const resetQuiz = () => {
    setCurrentQuestion(0);
    setSelectedOption('');
    setScore(0);
    setShowResult(false);
    setAnswers([]);
  };

  if (!quizData) return null;

  if (showResult) {
    return (
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        {showConfetti && (
          <Confetti
            width={windowSize.width}
            height={windowSize.height}
            recycle={false}
            numberOfPieces={500}
          />
        )}
        <Box sx={{ maxWidth: 800, mx: 'auto', p: 3, textAlign: 'center' }}>
          <Typography variant="h3" gutterBottom sx={{ mb: 4 }}>
            Quiz Results
          </Typography>
          
          <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
            <Typography variant="h4" sx={{ mb: 2 }}>
              Your Score: {score}/{quizData.quiz.length}
            </Typography>
            <Typography variant="h6" color={score === quizData.quiz.length ? 'success.main' : score > quizData.quiz.length / 2 ? 'warning.main' : 'error.main'}>
              {score === quizData.quiz.length ? 'Perfect! üéâ' : 
               score > quizData.quiz.length / 2 ? 'Good job! üëç' : 'Keep practicing! üí™'}
            </Typography>
          </Paper>

          <Box sx={{ mb: 4 }}>
            {quizData.quiz.map((question, index) => {
              const answer = answers.find(a => a.question === index);
              return (
                <Paper 
                  key={index} 
                  elevation={2} 
                  sx={{ 
                    p: 2, 
                    mb: 2,
                    borderLeft: `4px solid ${answer?.correct ? 'success.main' : 'error.main'}`
                  }}
                >
                  <Typography variant="h6" sx={{ mb: 1 }}>
                    {index + 1}. {question.question}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Typography variant="body1" sx={{ mr: 1 }}>Your answer:</Typography>
                    <Chip 
                      label={answer?.selected || 'Not answered'} 
                      color={answer?.correct ? 'success' : 'error'}
                      size="small"
                    />
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Typography variant="body1" sx={{ mr: 1 }}>Correct answer:</Typography>
                    <Chip label={question.answer} color="primary" size="small" />
                  </Box>
                </Paper>
              );
            })}
          </Box>

          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={resetQuiz}
            sx={{ mr: 2 }}
          >
            Try Again
          </Button>
          <Button
            variant="outlined"
            color="primary"
            size="large"
            onClick={() => navigate('/generate')}
          >
            New Quiz
          </Button>
        </Box>
      </motion.div>
    );
  }

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h5">
            Question {currentQuestion + 1} of {quizData.quiz.length}
          </Typography>
          <Chip label={`Score: ${score}`} color="primary" />
        </Box>

        <Paper elevation={3} sx={{ p: 4, mb: 3 }}>
          <Typography variant="h5" sx={{ mb: 3 }}>
            {quizData.quiz[currentQuestion].question}
          </Typography>

          <FormControl component="fieldset" sx={{ width: '100%' }}>
            <RadioGroup
              value={selectedOption}
              onChange={handleOptionChange}
            >
              {quizData.quiz[currentQuestion].options.length > 0 ? (
                quizData.quiz[currentQuestion].options.map((option, index) => (
                  <Paper 
                    key={index} 
                    elevation={1} 
                    sx={{ 
                      p: 2, 
                      mb: 1,
                      cursor: 'pointer',
                      '&:hover': {
                        backgroundColor: 'action.hover'
                      },
                      backgroundColor: selectedOption === option ? 'action.selected' : 'background.paper'
                    }}
                    onClick={() => setSelectedOption(option)}
                  >
                    <FormControlLabel 
                      value={option} 
                      control={<Radio />} 
                      label={option} 
                      sx={{ width: '100%', m: 0 }}
                    />
                  </Paper>
                ))
              ) : (
                <Typography variant="body1" color="text.secondary">
                  (Fill in the blank question - answer will be checked after submission)
                </Typography>
              )}
            </RadioGroup>
          </FormControl>
        </Paper>

        <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Button
            variant="outlined"
            color="primary"
            startIcon={<ArrowBack />}
            onClick={handlePrevious}
            disabled={currentQuestion === 0}
          >
            Previous
          </Button>
          
          <Button
            variant="contained"
            color="primary"
            endIcon={currentQuestion === quizData.quiz.length - 1 ? <Check /> : <ArrowForward />}
            onClick={handleNext}
            disabled={!selectedOption}
          >
            {currentQuestion === quizData.quiz.length - 1 ? 'Submit' : 'Next'}
          </Button>
        </Box>
      </Box>
    </motion.div>
  );
};

export default QuizDisplay;
