import React, { useState, useEffect } from "react";
import { jsPDF } from "jspdf";
import "./App.css";

const quizTypes = [
  { label: "Multiple Choice", value: "mcq" },
  { label: "True / False", value: "tf" },
  { label: "Fill in the Blanks", value: "fib" },
  { label: "Video Based", value: "video" },
  { label: "URL Based", value: "url" },
];

export default function App() {
  const [step, setStep] = useState("auth");
  const [authStep, setAuthStep] = useState("login");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [quizType, setQuizType] = useState("mcq");
  const [quiz, setQuiz] = useState([]);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [showLibrary, setShowLibrary] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  const [savedQuizzes, setSavedQuizzes] = useState([]);
  const [url, setUrl] = useState("");
  const [userProfile, setUserProfile] = useState(null);
  const [quizSource, setQuizSource] = useState("");

  // Mock user profile data
  const mockProfile = {
    username: "quizmaster",
    email: "quizmaster@example.com",
    joinedDate: "2023-05-15",
    quizzesCreated: savedQuizzes.length,
    lastActive: new Date().toLocaleDateString(),
  };

  useEffect(() => {
    // Update quiz count in profile whenever savedQuizzes changes
    if (userProfile) {
      setUserProfile({
        ...userProfile,
        quizzesCreated: savedQuizzes.length,
      });
    }
  }, [savedQuizzes]);

  const isValidUrl = (urlString) => {
    try {
      new URL(urlString);
      return true;
    } catch (e) {
      return false;
    }
  };

  const handleRegister = async () => {
    const res = await fetch("http://localhost:8000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    });
    const data = await res.json();
    alert(data.message);
    if (res.ok) {
      setUserProfile({
        username,
        email: `${username}@example.com`,
        joinedDate: new Date().toISOString().split('T')[0],
        quizzesCreated: 0,
        lastActive: new Date().toLocaleDateString(),
      });
      setAuthStep("login");
    }
  };

  const handleLogin = async () => {
    const formData = new URLSearchParams();
    formData.append("username", username);
    formData.append("password", password);

    const res = await fetch("http://localhost:8000/login", {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    alert(data.message);
    if (res.ok) {
      setUserProfile(mockProfile);
      setStep("upload");
    }
  };

  const handleUpload = async () => {
    if ((quizType !== "url" && !file) || (quizType === "url" && !url)) {
      alert("Please provide the required input");
      return;
    }

    if (quizType === "url" && !isValidUrl(url)) {
      alert("Please enter a valid URL");
      return;
    }

    setLoading(true);

    const formData = new FormData();
    if (file) formData.append("file", file);
    formData.append("quiz_type", quizType);
    if (url) formData.append("url", url);

    try {
      const res = await fetch("http://localhost:8000/generate-quiz", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.detail || "Failed to generate quiz");
      }

      const data = await res.json();
      setQuiz(data.quiz);
      setQuizSource(quizType === "url" ? "webpage" : file ? file.name : "content");
      setStep("preview");
    } catch (error) {
      alert(`Error: ${error.message}`);
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPosition = 10;

    doc.text(`Quiz Generated from: ${quizSource}`, 10, yPosition);
    yPosition += 10;

    quiz.forEach((q, i) => {
      doc.text(`${i + 1}. ${q.question}`, 10, yPosition);
      yPosition += 10;

      if (q.options && q.options.length > 0) {
        q.options.forEach((opt, j) => {
          doc.text(`   ${String.fromCharCode(65 + j)}. ${opt}`, 15, yPosition);
          yPosition += 7;
        });
      }

      doc.text(`Correct Answer: ${q.answer}`, 15, yPosition);
      yPosition += 15;

      if (yPosition > 280 && i < quiz.length - 1) {
        doc.addPage();
        yPosition = 10;
      }
    });

    const quizTitle = `quiz_${new Date().toISOString().slice(0, 10)}.pdf`;
    doc.save(quizTitle);
    setSuccess(true);

    // Save to library
    const quizDate = new Date().toLocaleDateString();
    const newQuiz = { 
      title: `Quiz ${savedQuizzes.length + 1} - ${quizDate}`,
      questions: [...quiz],
      dateCreated: quizDate,
      type: quizType,
      source: quizSource
    };
    setSavedQuizzes([...savedQuizzes, newQuiz]);
  };

  const handleCreateAnother = () => {
    setQuiz([]);
    setFile(null);
    setUrl("");
    setSuccess(false);
    setStep("upload");
  };

  const handleLogout = () => {
    setStep("auth");
    setUsername("");
    setPassword("");
    setUserProfile(null);
    setShowProfile(false);
  };

  const deleteQuiz = (index) => {
    const updatedQuizzes = savedQuizzes.filter((_, i) => i !== index);
    setSavedQuizzes(updatedQuizzes);
  };

  const getCurrentPageTitle = () => {
    switch(step) {
      case "upload": return "Create New Quiz";
      case "preview": return "Quiz Preview";
      case "auth": return authStep === "login" ? "Login" : "Register";
      default: return "AI Quiz Generator";
    }
  };

  return (
    <div className="app-container">
      {/* Header - shown on all pages except auth */}
      {step !== "auth" && (
        <header className="app-header">
          <button 
            className="menu-button" 
            onClick={() => {
              setShowLibrary(!showLibrary);
              setShowProfile(false);
            }}
          >
            â˜°
          </button>
          <h1>{getCurrentPageTitle()}</h1>
          <button 
            className="profile-button" 
            onClick={() => {
              setShowProfile(!showProfile);
              setShowLibrary(false);
            }}
          >
            ðŸ‘¤
          </button>
        </header>
      )}

      {/* Profile Dropdown */}
      {showProfile && userProfile && (
        <div className="profile-dropdown">
          <div className="profile-header">
            <h3>{userProfile.username}</h3>
            <button className="close-btn" onClick={() => setShowProfile(false)}>Ã—</button>
          </div>
          <div className="profile-details">
            <p><strong>Email:</strong> {userProfile.email}</p>
            <p><strong>Member since:</strong> {userProfile.joinedDate}</p>
            <p><strong>Quizzes created:</strong> {userProfile.quizzesCreated}</p>
            <p><strong>Last active:</strong> {userProfile.lastActive}</p>
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      )}

      {/* Library Sidebar */}
      {showLibrary && (
        <div className="library-sidebar">
          <div className="library-header">
            <h3>My Quiz Library</h3>
            <button className="close-btn" onClick={() => setShowLibrary(false)}>Ã—</button>
          </div>

          {savedQuizzes.length === 0 ? (
            <div className="empty-library">
              <p>No quizzes saved yet</p>
              <button 
                className="create-new-btn"
                onClick={() => {
                  setStep("upload");
                  setShowLibrary(false);
                }}
              >
                Create New Quiz
              </button>
            </div>
          ) : (
            <div className="quiz-list-container">
              <ul className="quiz-list">
                {savedQuizzes.map((quizItem, index) => (
                  <li key={index} className="quiz-item">
                    <div className="quiz-info">
                      <h4>{quizItem.title}</h4>
                      <p>Type: {quizTypes.find(t => t.value === quizItem.type)?.label || 'Unknown'}</p>
                      <p>Source: {quizItem.source || 'Unknown'}</p>
                    </div>
                    <div className="quiz-actions">
                      <button 
                        className="view-btn"
                        onClick={() => {
                          setQuiz(quizItem.questions);
                          setQuizSource(quizItem.source);
                          setStep("preview");
                          setShowLibrary(false);
                        }}
                      >
                        View
                      </button>
                      <button 
                        className="delete-btn"
                        onClick={() => deleteQuiz(index)}
                      >
                        Delete
                      </button>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {/* Overlay for when sidebar or profile is open */}
      {(showLibrary || showProfile) && (
        <div 
          className="overlay" 
          onClick={() => {
            setShowLibrary(false);
            setShowProfile(false);
          }}
        />
      )}

      <main className={`app-content ${showLibrary || showProfile ? 'shifted' : ''}`}>
        {step === "auth" && (
          <div className="auth-container">
            <h2>{authStep === "login" ? "Login" : "Register"}</h2>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={authStep === "login" ? handleLogin : handleRegister}>
              {authStep === "login" ? "Login" : "Register"}
            </button>
            <button 
              className="switch-auth"
              onClick={() => setAuthStep(authStep === "login" ? "register" : "login")}
            >
              {authStep === "login" ? "Need an account? Register" : "Already have an account? Login"}
            </button>
          </div>
        )}

        {step === "upload" && (
          <div className="upload-container">
            <h2>Create Quiz From Content</h2>

            {/* Quiz Type Selector */}
            <div className="quiz-type-selector">
              <label>
                Quiz Type:
                <select
                  value={quizType}
                  onChange={(e) => {
                    setQuizType(e.target.value);
                    setFile(null);
                    setUrl("");
                  }}
                >
                  {quizTypes.map((type) => (
                    <option key={type.value} value={type.value}>
                      {type.label}
                    </option>
                  ))}
                </select>
              </label>
            </div>

            {/* URL Input (shown only for URL quiz type) */}
            {quizType === "url" && (
              <div className="url-input">
                <input
                  type="url"
                  placeholder="Enter content URL (e.g., https://example.com)"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  className="url-field"
                />
                <p className="hint-text">Supports articles, blog posts, and educational content</p>
              </div>
            )}

            {/* File Upload (shown for all types except URL) */}
            {quizType !== "url" && (
              <div className="file-upload">
                <label className="file-upload-label">
                  <input
                    type="file"
                    accept={
                      quizType === "video" 
                        ? "video/*,.mp4,.webm,.mov" 
                        : ".pdf,.docx,.txt"
                    }
                    onChange={(e) => setFile(e.target.files[0])}
                    className="file-input"
                  />
                  <span className="file-upload-button">
                    {quizType === "video" ? "Choose Video File" : "Choose Document"}
                  </span>
                  {file && (
                    <span className="file-name">
                      {file.name} 
                      <button 
                        onClick={(e) => {
                          e.stopPropagation();
                          setFile(null);
                        }}
                        className="clear-file"
                      >
                        Ã—
                      </button>
                    </span>
                  )}
                </label>
                {quizType === "video" && (
                  <p className="hint-text">
                    Supported formats: MP4, WebM, MOV (max 25MB)
                  </p>
                )}
                {quizType !== "video" && (
                  <p className="hint-text">
                    Supported formats: PDF, DOCX, TXT
                  </p>
                )}
              </div>
            )}

            {/* Generate Button */}
            <button 
              className={`generate-btn ${loading ? 'loading' : ''}`} 
              onClick={handleUpload} 
              disabled={
                loading || 
                (quizType === "url" ? !url : !file) ||
                (quizType === "url" && !isValidUrl(url))
              }
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  {quizType === "video" ? "Processing Video..." : 
                   quizType === "url" ? "Analyzing URL..." : "Generating Quiz..."}
                </>
              ) : (
                "Generate Quiz"
              )}
            </button>
          </div>
        )}

        {step === "preview" && (
          <div className="preview-container">
            <div className="quiz-source">
              Generated from: {quizSource}
            </div>
            <h2>Quiz Preview</h2>
            <div className="quiz-questions">
              {quiz.map((q, idx) => (
                <div key={idx} className="question-card">
                  <div className="question-header">
                    <span className="question-number">Q{idx + 1}</span>
                    <span className="question-type">
                      {q.options && q.options.length > 0 ? "Multiple Choice" : "Short Answer"}
                    </span>
                  </div>
                  <div className="question-content">
                    <input
                      type="text"
                      value={q.question}
                      onChange={(e) => {
                        const newQuiz = [...quiz];
                        newQuiz[idx].question = e.target.value;
                        setQuiz(newQuiz);
                      }}
                      placeholder="Enter question"
                    />

                    {q.options && q.options.length > 0 && (
                      <ul className="options-list">
                        {q.options.map((opt, i) => (
                          <li key={i}>
                            <span className="option-letter">{String.fromCharCode(65 + i)}.</span>
                            <input
                              type="text"
                              value={opt}
                              onChange={(e) => {
                                const newQuiz = [...quiz];
                                newQuiz[idx].options[i] = e.target.value;
                                setQuiz(newQuiz);
                              }}
                              placeholder={`Option ${i + 1}`}
                            />
                          </li>
                        ))}
                      </ul>
                    )}

                    <div className="correct-answer">
                      <span>Correct Answer:</span>
                      {q.options && q.options.length > 0 ? (
                        <select
                          value={q.answer}
                          onChange={(e) => {
                            const newQuiz = [...quiz];
                            newQuiz[idx].answer = e.target.value;
                            setQuiz(newQuiz);
                          }}
                        >
                          {q.options.map((opt, i) => (
                            <option key={i} value={opt}>
                              {String.fromCharCode(65 + i)}. {opt}
                            </option>
                          ))}
                        </select>
                      ) : (
                        <input
                          type="text"
                          value={q.answer}
                          onChange={(e) => {
                            const newQuiz = [...quiz];
                            newQuiz[idx].answer = e.target.value;
                            setQuiz(newQuiz);
                          }}
                          placeholder="Enter correct answer"
                        />
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <div className="action-buttons">
              {!success && (
                <>
                  <button className="export-btn" onClick={exportToPDF}>
                    Export to PDF
                  </button>
                  <button className="another-btn" onClick={handleCreateAnother}>
                    Create Another Quiz
                  </button>
                </>
              )}

              {success && (
                <div className="success-message">
                  <p>ðŸŽ‰ Quiz exported successfully!</p>
                  <button className="home-btn" onClick={handleCreateAnother}>
                    Create Another Quiz
                  </button>
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
