import React, { useState } from "react";
import { jsPDF } from "jspdf";
import "./App.css";

const quizTypes = [
  { label: "Multiple Choice", value: "mcq" },
  { label: "True / False", value: "tf" },
  { label: "Fill in the Blanks", value: "fib" },
  { label: "Video Based", value: "video" }, // New type
  { label: "URL Based", value: "url" },     // New type
];

export default function App() {
  const [step, setStep] = useState("auth");
  const [authStep, setAuthStep] = useState("login");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [file, setFile] = useState(null);
  const [quizType, setQuizType] = useState("mcq");
  const [quiz, setQuiz] = useState([]);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [showLibrary, setShowLibrary] = useState(false);
  const [savedQuizzes, setSavedQuizzes] = useState([]);
  const [url, setUrl] = useState("");

  const handleRegister = async () => {
    const res = await fetch("http://localhost:8000/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ username, password }),
    });
    const data = await res.json();
    alert(data.message);
    if (res.ok) setAuthStep("login");
  };

  const handleLogin = async () => {
    const formData = new URLSearchParams();
    formData.append("username", username);
    formData.append("password", password);

    const res = await fetch("http://localhost:8000/login", {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    alert(data.message);
    if (res.ok) setStep("upload");
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file.");
      return;
    }
    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("quiz_type", quizType);

    try {
      const res = await fetch("http://localhost:8000/generate-quiz", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.detail || "Failed to generate quiz");
      }

      const data = await res.json();
      setQuiz(data.quiz);
      setStep("preview");
    } catch (error) {
      alert(`Error: ${error.message}`);
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    let yPosition = 10;

    quiz.forEach((q, i) => {
      doc.text(`${i + 1}. ${q.question}`, 10, yPosition);
      yPosition += 10;

      if (q.options && q.options.length > 0) {
        q.options.forEach((opt, j) => {
          doc.text(`   ${String.fromCharCode(65 + j)}. ${opt}`, 15, yPosition);
          yPosition += 7;
        });
      }

      doc.text(`Correct Answer: ${q.answer}`, 15, yPosition);
      yPosition += 15;

      if (yPosition > 280 && i < quiz.length - 1) {
        doc.addPage();
        yPosition = 10;
      }
    });

    doc.save("quiz.pdf");
    setSuccess(true);
    setSavedQuizzes([...savedQuizzes, { title: `Quiz ${savedQuizzes.length + 1}`, questions: quiz }]);
  };

  const handleCreateAnother = () => {
    setQuiz([]);
    setFile(null);
    setSuccess(false);
    setStep("upload");
  };
const isValidUrl = (urlString) => {
  try {
    new URL(urlString);
    return true;
  } catch (e) {
    return false;
  }
};
  const handleLogout = () => {
    setStep("auth");
    setUsername("");
    setPassword("");
  };

  return (
    <div className="app-container">
      {step !== "auth" && (
        <header className="app-header">
          <button className="menu-button" onClick={() => setShowLibrary(!showLibrary)}>
            â˜°
          </button>
          <h1>AI Quiz Generator</h1>
          <button className="profile-button" onClick={handleLogout}>
            ðŸ‘¤
          </button>
        </header>
      )}

      {showLibrary && (
        <div className="library-sidebar">
          <h3>My Library</h3>
          <button className="close-btn" onClick={() => setShowLibrary(false)}>Ã—</button>
          {savedQuizzes.length === 0 ? (
            <p>No saved quizzes yet</p>
          ) : (
            <ul>
              {savedQuizzes.map((quiz, index) => (
                <li key={index}>
                  <button onClick={() => {
                    setQuiz(quiz.questions);
                    setStep("preview");
                    setShowLibrary(false);
                  }}>
                    {quiz.title}
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      <main className={`app-content ${showLibrary ? 'shifted' : ''}`}>
        {step === "auth" && (
          <div className="auth-container">
            <h2>{authStep === "login" ? "Login" : "Register"}</h2>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={authStep === "login" ? handleLogin : handleRegister}>
              {authStep === "login" ? "Login" : "Register"}
            </button>
            <button 
              className="switch-auth"
              onClick={() => setAuthStep(authStep === "login" ? "register" : "login")}
            >
              {authStep === "login" ? "Need an account? Register" : "Already have an account? Login"}
            </button>
          </div>
        )}

{step === "upload" && (
  <div className="upload-container">
    <h2>Create Quiz From Content</h2>

    {/* Quiz Type Selector */}
    <div className="quiz-type-selector">
      <label>
        Quiz Type:
        <select
          value={quizType}
          onChange={(e) => {
            setQuizType(e.target.value);
            setFile(null);
            setUrl("");
          }}
        >
          {quizTypes.map((type) => (
            <option key={type.value} value={type.value}>
              {type.label}
            </option>
          ))}
        </select>
      </label>
    </div>

    {/* URL Input (shown only for URL quiz type) */}
    {quizType === "url" && (
      <div className="url-input">
        <input
          type="url"
          placeholder="Enter content URL (e.g., https://example.com)"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          className="url-field"
        />
        <p className="hint-text">Supports articles, blog posts, and educational content</p>
      </div>
    )}

    {/* File Upload (shown for all types except URL) */}
    {quizType !== "url" && (
      <div className="file-upload">
        <label className="file-upload-label">
          <input
            type="file"
            accept={
              quizType === "video" 
                ? "video/*,.mp4,.webm,.mov" 
                : ".pdf,.docx,.txt"
            }
            onChange={(e) => setFile(e.target.files[0])}
            className="file-input"
          />
          <span className="file-upload-button">
            {quizType === "video" ? "Choose Video File" : "Choose Document"}
          </span>
          {file && (
            <span className="file-name">
              {file.name} 
              <button 
                onClick={(e) => {
                  e.stopPropagation();
                  setFile(null);
                }}
                className="clear-file"
              >
                Ã—
              </button>
            </span>
          )}
        </label>
        {quizType === "video" && (
          <p className="hint-text">
            Supported formats: MP4, WebM, MOV (max 25MB)
          </p>
        )}
        {quizType !== "video" && (
          <p className="hint-text">
            Supported formats: PDF, DOCX, TXT
          </p>
        )}
      </div>
    )}

    {/* Generate Button with dynamic disabled state */}
    <button 
      className={`generate-btn ${loading ? 'loading' : ''}`} 
      onClick={handleUpload} 
      disabled={
        loading || 
        (quizType === "url" ? !url : !file) ||
        (quizType === "url" && !isValidUrl(url))
      }
    >
      {loading ? (
        <>
          <span className="spinner"></span>
          {quizType === "video" ? "Processing Video..." : 
           quizType === "url" ? "Analyzing URL..." : "Generating Quiz..."}
        </>
      ) : (
        "Generate Quiz"
      )}
    </button>
  </div>
)}

        {step === "preview" && (
          <div className="preview-container">
            <div className="quiz-source">
              {quizSource === "video" ? (
                <p>Generated from video content</p>
              ) : (
                <p>Generated from {file ? file.name : "webpage"}</p>
              )}
            </div>
            <h2>Quiz Preview</h2>
            <div className="quiz-questions">
              {quiz.map((q, idx) => (
                <div key={idx} className="question-card">
                  <p>
                    <strong>Question {idx + 1}:</strong>
                    <input
                      type="text"
                      value={q.question}
                      onChange={(e) => {
                        const newQuiz = [...quiz];
                        newQuiz[idx].question = e.target.value;
                        setQuiz(newQuiz);
                      }}
                    />
                  </p>

                  {q.options && q.options.length > 0 && (
                    <ul className="options-list">
                      {q.options.map((opt, i) => (
                        <li key={i}>
                          <input
                            type="text"
                            value={opt}
                            onChange={(e) => {
                              const newQuiz = [...quiz];
                              newQuiz[idx].options[i] = e.target.value;
                              setQuiz(newQuiz);
                            }}
                          />
                        </li>
                      ))}
                    </ul>
                  )}

                  <p className="correct-answer">
                    Correct Answer:
                    {q.options && q.options.length > 0 ? (
                      <select
                        value={q.answer}
                        onChange={(e) => {
                          const newQuiz = [...quiz];
                          newQuiz[idx].answer = e.target.value;
                          setQuiz(newQuiz);
                        }}
                      >
                        {q.options.map((opt, i) => (
                          <option key={i} value={opt}>
                            {opt}
                          </option>
                        ))}
                      </select>
                    ) : (
                      <input
                        type="text"
                        value={q.answer}
                        onChange={(e) => {
                          const newQuiz = [...quiz];
                          newQuiz[idx].answer = e.target.value;
                          setQuiz(newQuiz);
                        }}
                      />
                    )}
                  </p>
                </div>
              ))}
            </div>

            <div className="action-buttons">
              {!success && (
                <>
                  <button className="export-btn" onClick={exportToPDF}>
                    Export to PDF
                  </button>
                  <button className="another-btn" onClick={handleCreateAnother}>
                    Create Another
                  </button>
                </>
              )}

              {success && (
                <div className="success-message">
                  <p>ðŸŽ‰ Quiz exported successfully!</p>
                  <button className="home-btn" onClick={handleCreateAnother}>
                    Create Another Quiz
                  </button>
                </div>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}
