import React, { useState } from "react";
import { jsPDF } from "jspdf";

const quizTypes = [
  { label: "Multiple Choice", value: "mcq" },
  { label: "True / False", value: "tf" },
  { label: "Fill in the Blanks", value: "fib" },
];

export default function App() {
  const [step, setStep] = useState("home");
  const [file, setFile] = useState(null);
  const [quizType, setQuizType] = useState("mcq");
  const [quiz, setQuiz] = useState([]);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleStart = () => setStep("upload");

const handleUpload = async () => {
  if (!file) {
    alert("Please select a file.");
    return;
  }
  setLoading(true);

  const formData = new FormData();
  formData.append("file", file);
  formData.append("quiz_type", quizType);

  try {
    const res = await fetch("http://localhost:8000/generate-quiz", {
      method: "POST",
      body: formData,
      // Don't set Content-Type header - let the browser set it with boundary
    });
    if (!res.ok) throw new Error("Failed to generate quiz");
    const data = await res.json();
    setQuiz(data.quiz);
    setStep("preview");
  } catch (error) {
    alert(error.message);
  } finally {
    setLoading(false);
  }
};


const exportToPDF = () => {
  const doc = new jsPDF();
  quiz.forEach((q, i) => {
    doc.text(`${i + 1}. ${q.question}`, 10, 10 + i * 20);
    q.options.forEach((opt, j) => {
      doc.text(`  ${String.fromCharCode(65 + j)}. ${opt}`, 15, 16 + i * 20 + j * 6);
    });
  });
  doc.save("quiz.pdf");
};




  const handleExport = () => {
    alert("Exported (mock)!");
    setSuccess(true);
  };

  const handleCreateAnother = () => {
    setQuiz([]);
    setFile(null);
    setSuccess(false);
    setStep("upload");
  };

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "Arial" }}>
      {step === "home" && (
        <>
          <h1>AI-Powered Quiz Creator</h1>
          <p>Upload your lecture notes or textbook to instantly generate a quiz!</p>
          <button onClick={handleStart}>Start Now</button>
        </>
      )}

      {step === "upload" && (
        <>
          <h2>Upload Notes</h2>
          <input
            type="file"
            accept=".pdf,.docx,.txt"
            onChange={(e) => setFile(e.target.files[0])}
          />
          <div style={{ margin: "1rem 0" }}>
            <label>
              Quiz Type:{" "}
              <select
                value={quizType}
                onChange={(e) => setQuizType(e.target.value)}
              >
                {quizTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </label>
          </div>
          <button onClick={handleUpload} disabled={loading}>
            {loading ? "Generating Quiz..." : "Upload & Generate Quiz"}
          </button>
          <button onClick={() => setStep("home")} style={{ marginLeft: 10 }}>
            Cancel
          </button>
        </>
      )}

      {step === "preview" && (
        <>
          <h2>Quiz Preview</h2>
          // In your quiz preview component (replace part inside step === "preview")

{quiz.map((q, idx) => (
  <div
    key={idx}
    style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}
  >
    <p>
      <strong>Question {idx + 1}:</strong>{" "}
      <input
        type="text"
        value={q.question}
        onChange={(e) => {
          const newQuiz = [...quiz];
          newQuiz[idx].question = e.target.value;
          setQuiz(newQuiz);
        }}
        style={{ width: "100%" }}
      />
    </p>
    <ul>
      {q.options.map((opt, i) => (
        <li key={i}>
          <input
            type="text"
            value={opt}
            onChange={(e) => {
              const newQuiz = [...quiz];
              newQuiz[idx].options[i] = e.target.value;
              setQuiz(newQuiz);
            }}
          />
        </li>
      ))}
    </ul>
    <p>
      Correct Answer:{" "}
      <select
        value={q.answer}
        onChange={(e) => {
          const newQuiz = [...quiz];
          newQuiz[idx].answer = e.target.value;
          setQuiz(newQuiz);
        }}
      >
        {q.options.map((opt, i) => (
          <option key={i} value={opt}>
            {opt}
          </option>
        ))}
      </select>
    </p>
  </div>
))}


          {!success && (
            <>
              <button onClick={handleExport}>Export Quiz</button>
              <button onClick={handleCreateAnother} style={{ marginLeft: 10 }}>
                Create Another Quiz
              </button>
            </>
          )}

          {success && (
            <>
              <p>ðŸŽ‰ Your quiz has been generated and exported!</p>
              <button onClick={() => setStep("home")}>Go to Homepage</button>
            </>
          )}
        </>
      )}
    </div>
  );
}
