import React, { useState } from "react";

function App() {
  const [regUsername, setRegUsername] = useState("");
  const [regPassword, setRegPassword] = useState("");
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [message, setMessage] = useState("");

  const backendUrl = "http://127.0.0.1:8000"; // adjust if different

  const register = async () => {
    try {
      const res = await fetch(`${backendUrl}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: regUsername, password: regPassword }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.detail || "Register failed");
      setMessage("Register success: " + data.message);
    } catch (err) {
      setMessage("Register error: " + err.message);
    }
  };

  const login = async () => {
    try {
      const params = new URLSearchParams();
      params.append("username", loginUsername);
      params.append("password", loginPassword);

      const res = await fetch(`${backendUrl}/login`, {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: params.toString(),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.detail || "Login failed");
      setMessage("Login success: " + data.message);
    } catch (err) {
      setMessage("Login error: " + err.message);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Register</h2>
      <input
        placeholder="Username"
        value={regUsername}
        onChange={(e) => setRegUsername(e.target.value)}
      />
      <input
        placeholder="Password"
        type="password"
        value={regPassword}
        onChange={(e) => setRegPassword(e.target.value)}
      />
      <button onClick={register}>Register</button>

      <h2>Login</h2>
      <input
        placeholder="Username"
        value={loginUsername}
        onChange={(e) => setLoginUsername(e.target.value)}
      />
      <input
        placeholder="Password"
        type="password"
        value={loginPassword}
        onChange={(e) => setLoginPassword(e.target.value)}
      />
      <button onClick={login}>Login</button>

      <p>{message}</p>
    </div>
  );
}

export default App;
