import React, { useRef } from 'react';
import jsPDF from 'jspdf';

const QuizPreview = ({ quiz, onExport }) => {
  const quizRef = useRef();

  const handleExport = () => {
    const doc = new jsPDF();
    let y = 10;
    quiz.forEach((q, index) => {
      doc.text(`${index + 1}. ${q.question}`, 10, y);
      y += 10;
      if (q.options) {
        q.options.forEach((opt, i) => {
          doc.text(`   ${String.fromCharCode(65 + i)}. ${opt}`, 10, y);
          y += 10;
        });
      }
      doc.text(`Answer: ${q.answer}`, 10, y);
      y += 10;
    });
    doc.save('quiz.pdf');
    onExport();
  };

  return (
    <div className="quiz-preview" ref={quizRef}>
      <h2>Quiz Preview</h2>
      {quiz.map((q, index) => (
        <div key={index}>
          <p>{index + 1}. {q.question}</p>
          {q.options && (
            <ul>
              {q.options.map((opt, i) => (
                <li key={i}>{String.fromCharCode(65 + i)}. {opt}</li>
              ))}
            </ul>
          )}
          <p><strong>Answer:</strong> {q.answer}</p>
        </div>
      ))}
      <button onClick={handleExport}>Export to PDF</button>
    </div>
  );
};

export default QuizPreview;
