import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { motion } from 'framer-motion';
import axios from 'axios';
import { toast } from 'react-toastify';
import { 
  Box, 
  Typography, 
  Button, 
  CircularProgress,
  Paper,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel
} from '@mui/material';
import { CloudUpload, Description, PictureAsPdf, InsertDriveFile } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const QuizGenerator = ({ setQuizData }) => {
  const [file, setFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [quizType, setQuizType] = useState('mcq');
  const navigate = useNavigate();

  const onDrop = useCallback((acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      setFile(acceptedFiles[0]);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxFiles: 1
  });

  const handleSubmit = async () => {
    if (!file) {
      toast.error('Please select a file first');
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('quiz_type', quizType);

    try {
      const response = await axios.post('http://localhost:8000/generate-quiz', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setQuizData(response.data);
      navigate('/quiz');
      toast.success('Quiz generated successfully!');
    } catch (error) {
      console.error('Error generating quiz:', error);
      toast.error(error.response?.data?.detail || 'Failed to generate quiz');
    } finally {
      setIsLoading(false);
    }
  };

  const getFileIcon = () => {
    if (!file) return <CloudUpload sx={{ fontSize: 60 }} />;
    
    const extension = file.name.split('.').pop().toLowerCase();
    switch (extension) {
      case 'pdf':
        return <PictureAsPdf sx={{ fontSize: 60 }} color="error" />;
      case 'docx':
        return <Description sx={{ fontSize: 60 }} color="primary" />;
      case 'txt':
        return <InsertDriveFile sx={{ fontSize: 60 }} color="secondary" />;
      default:
        return <Description sx={{ fontSize: 60 }} />;
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="quiz-generator"
    >
      <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
        <Typography variant="h4" gutterBottom align="center" sx={{ mb: 4 }}>
          Generate Quiz from Document
        </Typography>
        
        <Paper 
          elevation={3} 
          sx={{ 
            p: 4, 
            mb: 4,
            border: '2px dashed',
            borderColor: isDragActive ? 'primary.main' : 'divider',
            backgroundColor: isDragActive ? 'action.hover' : 'background.paper',
            transition: 'all 0.3s ease'
          }}
          {...getRootProps()}
        >
          <input {...getInputProps()} />
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            {getFileIcon()}
            <Typography variant="body1" sx={{ mt: 2, mb: 1 }}>
              {isDragActive ? (
                'Drop the file here'
              ) : file ? (
                `Selected: ${file.name}`
              ) : (
                'Drag & drop a file here, or click to select'
              )}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Supported formats: PDF, DOCX, TXT
            </Typography>
          </Box>
        </Paper>

        <FormControl component="fieldset" sx={{ mb: 4, width: '100%' }}>
          <FormLabel component="legend">Quiz Type</FormLabel>
          <RadioGroup 
            row 
            value={quizType} 
            onChange={(e) => setQuizType(e.target.value)}
            sx={{ justifyContent: 'space-around', mt: 1 }}
          >
            <FormControlLabel 
              value="mcq" 
              control={<Radio color="primary" />} 
              label="Multiple Choice" 
            />
            <FormControlLabel 
              value="tf" 
              control={<Radio color="primary" />} 
              label="True/False" 
            />
            <FormControlLabel 
              value="fib" 
              control={<Radio color="primary" />} 
              label="Fill in Blank" 
            />
          </RadioGroup>
        </FormControl>

        <Box sx={{ display: 'flex', justifyContent: 'center' }}>
          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={handleSubmit}
            disabled={!file || isLoading}
            startIcon={isLoading ? <CircularProgress size={20} color="inherit" /> : null}
            sx={{ px: 6, py: 1.5, fontSize: '1.1rem' }}
          >
            {isLoading ? 'Generating...' : 'Generate Quiz'}
          </Button>
        </Box>
      </Box>
    </motion.div>
  );
};

export default QuizGenerator;
